    return (
        <div className="container-fluid mt-3 mb-3">
            <div className="row row-cols-1 row-cols-md-4 g-4">
                {
                    productos.map(producto => {
                        return (
                            <div className="col" key={producto.id}>
                                <div className="card">
                                    <img src={producto.imagen} className="card-img-top" alt="..." />
